mapper.select:
SELECT 
FROM video
WHERE id IN

    #{id}

ORDER BY FIELD(id,

    #{id}

)

SELECT FROM video WHERE status = 'approved'
AND title LIKE CONCAT('%', #{title}, '%')
AND ulid < #{cursor, typeHandler=com.catowl.chatroom.handler.UlidTypeHandler}
ORDER BY ulid DESC LIMIT #{limit}

mapper.update:
UPDATE video
title = #{title}, description = #{description}, video_url = #{videoUrl}, cover_image_url = #{coverImageUrl}, status = #{status}, audit_notes = #{auditNotes}, auditor_user_id = #{auditorUserId}, audited_at = #{auditedAt},
WHERE id = #{id}

UPDATE video
SET view_count = view_count + #{increment}
WHERE id = #{id}

UPDATE video
SET like_count = like_count + #{increment}
WHERE id = #{id}

UPDATE video
SET like_count = CASE
                     WHEN like_count - #{decrement} < 0 THEN 0
                     ELSE like_count - #{decrement}
    END
WHERE id = #{id}
